package com.tend.acd;

import org.junit.Test;

import java.io.File;
import java.io.IOException;
import java.lang.reflect.InvocationTargetException;
import java.lang.reflect.Method;
import java.net.MalformedURLException;
import java.net.URL;
import java.net.URLClassLoader;
import java.nio.file.Path;
import java.nio.file.Paths;

import static org.junit.Assert.*;

public class UtilTest extends ApplicationTests {

    @Test
    public void saveBase64Image() throws IOException {
        String image = "data:image/png;base64,";
        File savedFile = Util.saveBase64Image(image);
        Util.logger.trace("Save success to " + savedFile.getAbsolutePath());
    }

    @Test
    public void classLoader() throws ClassNotFoundException, NoSuchMethodException, IOException, InvocationTargetException, IllegalAccessException, InstantiationException {
        Path filePath = Paths.get(Util.getAppPath(),"lib\\Image_Recognition.jar");
        Util.logger.trace("loading " + filePath.toString());
        URL url = filePath.toUri().toURL();
        URLClassLoader jar = new URLClassLoader(
                new URL[] {url},
                this.getClass().getClassLoader()
        );
        Class imageService = Class.forName("Cancer_Img_Recognition.Class1", true, jar);
        Object instance = imageService.newInstance();
        Method imageRecog =  imageService.getDeclaredMethod("Cancer_Img_Recognition",int.class,Object[].class);
        Object[] result = (Object[])imageRecog.invoke(instance,1, new Object[]{"","0", "BR"});
        Util.logger.trace("image service warming up success =>" + result[0].toString());
        String base64String = Util.getBase64String("init.jpg");
        result = (Object[])imageRecog.invoke(instance,1, new Object[]{base64String, 1, "BR"});
        Util.logger.trace("image service first call success =>" + result[0].toString());
    }
}